import React, { useState, useEffect } from 'react';
import { Container, Row, Col, Card, Button, Alert, Badge, Form, ListGroup } from 'react-bootstrap';
import { useParams, useNavigate } from 'react-router-dom';
import { getProduct, createReview, getCurrentUser } from '../services/marketplaceApi';

const ProductDetailPage = () => {
  const { productId } = useParams();
  const navigate = useNavigate();
  const [product, setProduct] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [quantity, setQuantity] = useState(1);
  const [user, setUser] = useState(null);
  const [reviewForm, setReviewForm] = useState({ rating: 5, comment: '' });
  const [reviewSubmitting, setReviewSubmitting] = useState(false);
  const [reviewError, setReviewError] = useState(null);

  useEffect(() => {
    // Check if user is logged in
    const currentUser = getCurrentUser();
    setUser(currentUser);

    // Fetch product details
    fetchProduct();
  }, [productId]);

  const fetchProduct = async () => {
    setLoading(true);
    setError(null);
    try {
      const data = await getProduct(productId);
      setProduct(data);
      setLoading(false);
    } catch (err) {
      setError('Failed to load product details. Please try again.');
      setLoading(false);
    }
  };

  const handleQuantityChange = (e) => {
    const value = parseInt(e.target.value);
    if (value > 0 && value <= (product?.stock_quantity || 1)) {
      setQuantity(value);
    }
  };

  const handleAddToCart = () => {
    if (!user) {
      navigate('/auth');
      return;
    }

    // Get existing cart from localStorage or initialize empty cart
    const existingCart = JSON.parse(localStorage.getItem('cart') || '[]');

    // Check if product already in cart
    const existingItemIndex = existingCart.findIndex(item => item.product_id === product.id);

    if (existingItemIndex >= 0) {
      // Update quantity if product already in cart
      existingCart[existingItemIndex].quantity += quantity;
    } else {
      // Add new item to cart
      existingCart.push({
        product_id: product.id,
        name: product.name,
        price: product.price,
        image_url: product.image_url,
        quantity: quantity,
        seller_id: product.seller_id,
        seller_name: product.seller?.full_name || 'Unknown Seller'
      });
    }

    // Save updated cart to localStorage
    localStorage.setItem('cart', JSON.stringify(existingCart));

    // Show success message or redirect to cart
    alert('Product added to cart!');
  };

  const handleReviewChange = (e) => {
    const { name, value } = e.target;
    setReviewForm({
      ...reviewForm,
      [name]: name === 'rating' ? parseInt(value) : value
    });
  };

  const handleReviewSubmit = async (e) => {
    e.preventDefault();
    if (!user) {
      navigate('/auth');
      return;
    }

    setReviewSubmitting(true);
    setReviewError(null);

    try {
      await createReview(productId, reviewForm);
      setReviewSubmitting(false);
      setReviewForm({ rating: 5, comment: '' });
      // Refresh product to show the new review
      fetchProduct();
    } catch (err) {
      setReviewError(err.response?.data?.error || 'Failed to submit review. Please try again.');
      setReviewSubmitting(false);
    }
  };

  const handleBackToMarketplace = () => {
    navigate('/marketplace');
  };

  if (loading) {
    return (
      <Container className="py-5">
        <div className="text-center">
          <div className="spinner-border" role="status">
            <span className="visually-hidden">Loading...</span>
          </div>
          <p className="mt-2">Loading product details...</p>
        </div>
      </Container>
    );
  }

  if (error || !product) {
    return (
      <Container className="py-5">
        <Alert variant="danger">{error || 'Product not found'}</Alert>
        <Button variant="outline-primary" onClick={handleBackToMarketplace}>
          Back to Marketplace
        </Button>
      </Container>
    );
  }

  return (
    <Container className="py-5">
      <Button 
        variant="outline-primary" 
        className="mb-4"
        onClick={handleBackToMarketplace}
      >
        <i className="bi bi-arrow-left me-2"></i> Back to Marketplace
      </Button>

      <Row>
        <Col md={5}>
          {product.image_url ? (
            <img 
              src={product.image_url} 
              alt={product.name} 
              className="img-fluid rounded"
              style={{ maxHeight: '400px', width: '100%', objectFit: 'cover' }}
            />
          ) : (
            <div className="text-center bg-light py-5 rounded">
              <i className="bi bi-image" style={{ fontSize: '5rem' }}></i>
              <p>No image available</p>
            </div>
          )}
        </Col>

        <Col md={7}>
          <h2>{product.name}</h2>
          <Badge bg="secondary" className="mb-3">{product.category}</Badge>

          <div className="d-flex align-items-center mb-3">
            {product.avg_rating ? (
              <div className="me-2">
                <i className="bi bi-star-fill text-warning"></i>
                <span className="ms-1">{product.avg_rating} ({product.reviews?.length || 0} reviews)</span>
              </div>
            ) : (
              <span className="text-muted">No reviews yet</span>
            )}
          </div>

          <h3 className="text-primary mb-3">${product.price.toFixed(2)}</h3>

          <p className="mb-4">{product.description}</p>

          <div className="mb-4">
            <p className="mb-1"><strong>Seller:</strong> {product.seller?.full_name || 'Unknown Seller'}</p>
            <p className="mb-1"><strong>Stock:</strong> {product.stock_quantity} available</p>
          </div>

          {user?.role === 'buyer' && (
            <div className="d-flex align-items-center mb-4">
              <Form.Group className="me-3" style={{ width: '100px' }}>
                <Form.Label>Quantity</Form.Label>
                <Form.Control 
                  type="number" 
                  min="1" 
                  max={product.stock_quantity}
                  value={quantity}
                  onChange={handleQuantityChange}
                />
              </Form.Group>

              <Button 
                variant="success" 
                size="lg"
                onClick={handleAddToCart}
                disabled={product.stock_quantity < 1}
              >
                <i className="bi bi-cart-plus me-2"></i>
                Add to Cart
              </Button>
            </div>
          )}

          {product.stock_quantity < 1 && (
            <Alert variant="warning">This product is currently out of stock.</Alert>
          )}
        </Col>
      </Row>

      <hr className="my-5" />

      <Row className="mb-5">
        <Col>
          <h4 className="mb-4">Reviews</h4>

          {user?.role === 'buyer' && (
            <Card className="mb-4">
              <Card.Body>
                <h5 className="mb-3">Write a Review</h5>
                {reviewError && <Alert variant="danger">{reviewError}</Alert>}

                <Form onSubmit={handleReviewSubmit}>
                  <Form.Group className="mb-3">
                    <Form.Label>Rating</Form.Label>
                    <Form.Select 
                      name="rating"
                      value={reviewForm.rating}
                      onChange={handleReviewChange}
                      required
                    >
                      <option value="5">5 - Excellent</option>
                      <option value="4">4 - Very Good</option>
                      <option value="3">3 - Good</option>
                      <option value="2">2 - Fair</option>
                      <option value="1">1 - Poor</option>
                    </Form.Select>
                  </Form.Group>

                  <Form.Group className="mb-3">
                    <Form.Label>Comment</Form.Label>
                    <Form.Control 
                      as="textarea" 
                      rows={3}
                      name="comment"
                      value={reviewForm.comment}
                      onChange={handleReviewChange}
                      placeholder="Share your experience with this product"
                    />
                  </Form.Group>

                  <Button 
                    type="submit" 
                    variant="primary"
                    disabled={reviewSubmitting}
                  >
                    {reviewSubmitting ? 'Submitting...' : 'Submit Review'}
                  </Button>
                </Form>
              </Card.Body>
            </Card>
          )}

          {product.reviews && product.reviews.length > 0 ? (
            <ListGroup>
              {product.reviews.map(review => (
                <ListGroup.Item key={review.id} className="mb-3">
                  <div className="d-flex justify-content-between align-items-center mb-2">
                    <div>
                      <span className="fw-bold me-2">Rating:</span>
                      {[...Array(review.rating)].map((_, i) => (
                        <i key={i} className="bi bi-star-fill text-warning"></i>
                      ))}
                      {[...Array(5 - review.rating)].map((_, i) => (
                        <i key={i} className="bi bi-star text-warning"></i>
                      ))}
                    </div>
                    <small className="text-muted">
                      {new Date(review.created_at).toLocaleDateString()}
                    </small>
                  </div>
                  {review.comment && <p className="mb-0">{review.comment}</p>}
                </ListGroup.Item>
              ))}
            </ListGroup>
          ) : (
            <Alert variant="info">No reviews yet. Be the first to review this product!</Alert>
          )}
        </Col>
      </Row>
    </Container>
  );
};

export default ProductDetailPage;
