import React from 'react';
import { Navbar, Nav, Container, Badge } from 'react-bootstrap';
import { Link } from 'react-router-dom';
import { getCurrentUser, logoutUser } from '../services/marketplaceApi';

const Navigation = () => {
  const user = getCurrentUser();
  const cartItems = JSON.parse(localStorage.getItem('cart') || '[]');
  
  const handleLogout = () => {
    logoutUser();
    window.location.href = '/';
  };
  
  return (
    <Navbar bg="dark" variant="dark" expand="lg">
      <Container>
        <Navbar.Brand as={Link} to="/">Crop Monitoring App</Navbar.Brand>
        <Navbar.Toggle aria-controls="basic-navbar-nav" />
        <Navbar.Collapse id="basic-navbar-nav">
          <Nav className="me-auto">
            <Nav.Link as={Link} to="/">Home</Nav.Link>
            <Nav.Link as={Link} to="/dashboard">Dashboard</Nav.Link>
            <Nav.Link as={Link} to="/weather">Weather</Nav.Link>
            <Nav.Link as={Link} to="/chatbot">AI Assistant</Nav.Link>
            <Nav.Link as={Link} to="/marketplace">Marketplace</Nav.Link>
          </Nav>
          <Nav>
            {user ? (
              <>
                {user.role === 'buyer' && (
                  <Nav.Link as={Link} to="/marketplace/cart">
                    <i className="bi bi-cart3"></i>
                    {cartItems.length > 0 && (
                      <Badge pill bg="danger" className="ms-1">
                        {cartItems.length}
                      </Badge>
                    )}
                  </Nav.Link>
                )}
                <Nav.Link as={Link} to={user.role === 'seller' ? '/marketplace/seller' : '/marketplace/orders'}>
                  {user.role === 'seller' ? 'Seller Dashboard' : 'My Orders'}
                </Nav.Link>
                <Nav.Link onClick={handleLogout}>Logout</Nav.Link>
              </>
            ) : (
              <Nav.Link as={Link} to="/auth">Login / Register</Nav.Link>
            )}
          </Nav>
        </Navbar.Collapse>
      </Container>
    </Navbar>
  );
};

export default Navigation;
