import React, { useState } from 'react';
import { Form, Button, Alert, Card } from 'react-bootstrap';
import { registerUser } from '../../services/marketplaceApi';

const RegisterForm = ({ onRegisterSuccess }) => {
  const [formData, setFormData] = useState({
    email: '',
    password: '',
    full_name: '',
    role: 'buyer'
  });
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError(null);

    try {
      const response = await registerUser(formData);
      setLoading(false);
      if (onRegisterSuccess) {
        onRegisterSuccess(response.user);
      }
    } catch (err) {
      setLoading(false);
      setError(err.response?.data?.error || 'Registration failed. Please try again.');
    }
  };

  return (
    <Card className="p-4 shadow-sm">
      <Card.Title className="text-center mb-4">Create an Account</Card.Title>
      {error && <Alert variant="danger">{error}</Alert>}
      <Form onSubmit={handleSubmit}>
        <Form.Group className="mb-3" controlId="fullName">
          <Form.Label>Full Name</Form.Label>
          <Form.Control
            type="text"
            name="full_name"
            value={formData.full_name}
            onChange={handleChange}
            placeholder="Enter your full name"
            required
          />
        </Form.Group>

        <Form.Group className="mb-3" controlId="email">
          <Form.Label>Email address</Form.Label>
          <Form.Control
            type="email"
            name="email"
            value={formData.email}
            onChange={handleChange}
            placeholder="Enter your email"
            required
          />
        </Form.Group>

        <Form.Group className="mb-3" controlId="password">
          <Form.Label>Password</Form.Label>
          <Form.Control
            type="password"
            name="password"
            value={formData.password}
            onChange={handleChange}
            placeholder="Create a password"
            required
            minLength="6"
          />
        </Form.Group>

        <Form.Group className="mb-3" controlId="role">
          <Form.Label>I want to</Form.Label>
          <Form.Select 
            name="role" 
            value={formData.role}
            onChange={handleChange}
            required
          >
            <option value="buyer">Buy sustainable inputs</option>
            <option value="seller">Sell sustainable inputs</option>
          </Form.Select>
        </Form.Group>

        <Button
          variant="primary"
          type="submit"
          className="w-100 mt-3"
          disabled={loading}
        >
          {loading ? 'Creating Account...' : 'Register'}
        </Button>
      </Form>
    </Card>
  );
};

export default RegisterForm;
