import React, { useState, useEffect } from 'react';
import { Container, Row, Col, Card, Button, Form, Alert } from 'react-bootstrap';
import { useNavigate } from 'react-router-dom';
import { getProducts, getCurrentUser } from '../services/marketplaceApi';

const MarketplacePage = () => {
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [category, setCategory] = useState('');
  const [user, setUser] = useState(null);
  const navigate = useNavigate();

  // Categories for sustainable inputs
  const categories = [
    'Organic Fertilizers',
    'Biopesticides',
    'Organic Seeds',
    'Soil Amendments',
    'Composting Supplies',
    'Irrigation Systems',
    'Sustainable Tools'
  ];

  useEffect(() => {
    // Check if user is logged in
    const currentUser = getCurrentUser();
    setUser(currentUser);

    // Fetch products
    fetchProducts();
  }, []);

  const fetchProducts = async (filters = {}) => {
    setLoading(true);
    setError(null);
    try {
      const response = await getProducts(filters);
      setProducts(response.products);
      setLoading(false);
    } catch (err) {
      setError('Failed to load products. Please try again.');
      setLoading(false);
    }
  };

  const handleCategoryChange = (e) => {
    const selectedCategory = e.target.value;
    setCategory(selectedCategory);
    fetchProducts(selectedCategory ? { category: selectedCategory } : {});
  };

  const handleProductClick = (productId) => {
    navigate(`/marketplace/products/${productId}`);
  };

  const handleLoginClick = () => {
    navigate('/auth');
  };

  const handleSellerDashboardClick = () => {
    navigate('/marketplace/seller');
  };

  const handleBuyerOrdersClick = () => {
    navigate('/marketplace/orders');
  };

  return (
    <Container className="py-4">
      <h2 className="mb-4">Sustainable Farming Inputs Marketplace</h2>

      {!user && (
        <Alert variant="info" className="mb-4">
          <p className="mb-0">Sign in to place orders or list your sustainable products.</p>
          <Button variant="primary" size="sm" className="mt-2" onClick={handleLoginClick}>
            Login / Register
          </Button>
        </Alert>
      )}

      {user && (
        <div className="mb-4">
          <Row>
            <Col>
              <h5>Welcome, {user.full_name}!</h5>
              <p>You are logged in as a {user.role === 'buyer' ? 'Buyer' : 'Seller'}.</p>
            </Col>
            <Col className="text-end">
              {user.role === 'seller' ? (
                <Button variant="success" onClick={handleSellerDashboardClick}>
                  Seller Dashboard
                </Button>
              ) : (
                <Button variant="primary" onClick={handleBuyerOrdersClick}>
                  My Orders
                </Button>
              )}
            </Col>
          </Row>
        </div>
      )}

      <Row className="mb-4">
        <Col md={4}>
          <Form.Group>
            <Form.Label>Filter by Category</Form.Label>
            <Form.Select 
              value={category} 
              onChange={handleCategoryChange}
            >
              <option value="">All Categories</option>
              {categories.map((cat, index) => (
                <option key={index} value={cat}>{cat}</option>
              ))}
            </Form.Select>
          </Form.Group>
        </Col>
      </Row>

      {error && <Alert variant="danger">{error}</Alert>}

      {loading ? (
        <div className="text-center py-5">
          <div className="spinner-border" role="status">
            <span className="visually-hidden">Loading...</span>
          </div>
          <p className="mt-2">Loading products...</p>
        </div>
      ) : products.length === 0 ? (
        <Alert variant="info">
          No products found. {user?.role === 'seller' && 'Add your sustainable products to get started!'}
        </Alert>
      ) : (
        <Row>
          {products.map(product => (
            <Col key={product.id} md={4} className="mb-4">
              <Card className="h-100 product-card">
                {product.image_url ? (
                  <Card.Img 
                    variant="top" 
                    src={product.image_url} 
                    alt={product.name}
                    style={{ height: '180px', objectFit: 'cover' }}
                  />
                ) : (
                  <div className="text-center bg-light py-5">
                    <i className="bi bi-image" style={{ fontSize: '3rem' }}></i>
                  </div>
                )}
                <Card.Body>
                  <Card.Title>{product.name}</Card.Title>
                  <Card.Text className="text-muted mb-2">{product.category}</Card.Text>
                  <Card.Text className="mb-2">
                    ${product.price.toFixed(2)}
                  </Card.Text>
                  <Card.Text className="small text-truncate mb-3">
                    {product.description}
                  </Card.Text>
                  <div className="d-flex justify-content-between align-items-center">
                    <small className="text-muted">By: {product.seller?.full_name || 'Unknown Seller'}</small>
                    <div>
                      {product.avg_rating && (
                        <span className="me-2">
                          <i className="bi bi-star-fill text-warning"></i> {product.avg_rating}
                        </span>
                      )}
                      <Button 
                        variant="outline-primary" 
                        size="sm"
                        onClick={() => handleProductClick(product.id)}
                      >
                        View Details
                      </Button>
                    </div>
                  </div>
                </Card.Body>
              </Card>
            </Col>
          ))}
        </Row>
      )}
    </Container>
  );
};

export default MarketplacePage;
