import React, { useState, useEffect } from 'react';
import { Container, Row, Col, Card, Button, Alert, Form, ListGroup } from 'react-bootstrap';
import { useNavigate } from 'react-router-dom';
import { createOrder, getCurrentUser } from '../services/marketplaceApi';

const CartPage = () => {
  const [cartItems, setCartItems] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [shippingAddress, setShippingAddress] = useState('');
  const [user, setUser] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    // Check if user is logged in
    const currentUser = getCurrentUser();
    setUser(currentUser);

    // Redirect to login if not logged in
    if (!currentUser) {
      navigate('/auth');
      return;
    }

    // Load cart items from localStorage
    const storedCart = JSON.parse(localStorage.getItem('cart') || '[]');
    setCartItems(storedCart);
  }, [navigate]);

  const handleQuantityChange = (index, value) => {
    const updatedCart = [...cartItems];
    updatedCart[index].quantity = value;
    setCartItems(updatedCart);
    localStorage.setItem('cart', JSON.stringify(updatedCart));
  };

  const handleRemoveItem = (index) => {
    const updatedCart = [...cartItems];
    updatedCart.splice(index, 1);
    setCartItems(updatedCart);
    localStorage.setItem('cart', JSON.stringify(updatedCart));
  };

  const handleAddressChange = (e) => {
    setShippingAddress(e.target.value);
  };

  const calculateTotal = () => {
    return cartItems.reduce((total, item) => total + (item.price * item.quantity), 0);
  };

  const handleCheckout = async () => {
    if (!shippingAddress.trim()) {
      setError('Please enter a shipping address');
      return;
    }

    setLoading(true);
    setError(null);

    try {
      // Format order data
      const orderData = {
        items: cartItems.map(item => ({
          product_id: item.product_id,
          quantity: item.quantity
        })),
        shipping_address: shippingAddress
      };

      // Create order
      const response = await createOrder(orderData);

      // Clear cart
      localStorage.setItem('cart', '[]');

      // Redirect to order confirmation
      navigate(`/marketplace/orders/${response.order.id}`, { 
        state: { orderSuccess: true } 
      });
    } catch (err) {
      setError(err.response?.data?.error || 'Failed to create order. Please try again.');
      setLoading(false);
    }
  };

  const handleContinueShopping = () => {
    navigate('/marketplace');
  };

  if (!user) {
    return null; // Will redirect in useEffect
  }

  return (
    <Container className="py-5">
      <h2 className="mb-4">Your Shopping Cart</h2>

      {error && <Alert variant="danger">{error}</Alert>}

      {cartItems.length === 0 ? (
        <div className="text-center py-5">
          <i className="bi bi-cart" style={{ fontSize: '3rem' }}></i>
          <h4 className="mt-3">Your cart is empty</h4>
          <p className="text-muted">Add some sustainable farming inputs to get started!</p>
          <Button 
            variant="primary" 
            className="mt-3"
            onClick={handleContinueShopping}
          >
            Continue Shopping
          </Button>
        </div>
      ) : (
        <Row>
          <Col md={8}>
            <Card className="mb-4">
              <Card.Header>
                <h5 className="mb-0">Cart Items ({cartItems.length})</h5>
              </Card.Header>
              <ListGroup variant="flush">
                {cartItems.map((item, index) => (
                  <ListGroup.Item key={index} className="py-3">
                    <Row className="align-items-center">
                      <Col xs={2} className="text-center">
                        {item.image_url ? (
                          <img 
                            src={item.image_url} 
                            alt={item.name} 
                            className="img-fluid rounded"
                            style={{ maxHeight: '60px' }}
                          />
                        ) : (
                          <div className="bg-light rounded p-2 text-center">
                            <i className="bi bi-image"></i>
                          </div>
                        )}
                      </Col>
                      <Col xs={4}>
                        <h6 className="mb-1">{item.name}</h6>
                        <small className="text-muted">Seller: {item.seller_name}</small>
                      </Col>
                      <Col xs={2} className="text-center">
                        ${item.price.toFixed(2)}
                      </Col>
                      <Col xs={2}>
                        <Form.Control
                          type="number"
                          min="1"
                          value={item.quantity}
                          onChange={(e) => handleQuantityChange(index, parseInt(e.target.value))}
                          size="sm"
                        />
                      </Col>
                      <Col xs={1} className="text-center">
                        ${(item.price * item.quantity).toFixed(2)}
                      </Col>
                      <Col xs={1} className="text-center">
                        <Button 
                          variant="link" 
                          className="text-danger p-0"
                          onClick={() => handleRemoveItem(index)}
                        >
                          <i className="bi bi-trash"></i>
                        </Button>
                      </Col>
                    </Row>
                  </ListGroup.Item>
                ))}
              </ListGroup>
            </Card>

            <Button 
              variant="outline-primary"
              onClick={handleContinueShopping}
            >
              <i className="bi bi-arrow-left me-2"></i> Continue Shopping
            </Button>
          </Col>

          <Col md={4}>
            <Card>
              <Card.Header>
                <h5 className="mb-0">Order Summary</h5>
              </Card.Header>
              <Card.Body>
                <div className="d-flex justify-content-between mb-3">
                  <span>Subtotal:</span>
                  <span>${calculateTotal().toFixed(2)}</span>
                </div>
                <div className="d-flex justify-content-between mb-3">
                  <span>Shipping:</span>
                  <span>Free</span>
                </div>
                <hr />
                <div className="d-flex justify-content-between mb-4">
                  <strong>Total:</strong>
                  <strong>${calculateTotal().toFixed(2)}</strong>
                </div>

                <Form.Group className="mb-3">
                  <Form.Label>Shipping Address</Form.Label>
                  <Form.Control
                    as="textarea"
                    rows={3}
                    value={shippingAddress}
                    onChange={handleAddressChange}
                    placeholder="Enter your complete shipping address"
                    required
                  />
                </Form.Group>

                <Button
                  variant="success"
                  className="w-100"
                  onClick={handleCheckout}
                  disabled={loading || cartItems.length === 0}
                >
                  {loading ? 'Processing...' : 'Proceed to Checkout'}
                </Button>
              </Card.Body>
            </Card>
          </Col>
        </Row>
      )}
    </Container>
  );
};

export default CartPage;
